mapper.select:

SELECT S.STADIUM_NAME ,S.STADIUM_PHOTO ,S.STADIUM_INFO ,S.STADIUM_ADDR 
 FROM STADIUM S 
 limit #{startRow}, #{pageSize};

 	SELECT S.STADIUM_NAME stadiumName,S.STADIUM_PHOTO stadiumPhoto,S.STADIUM_INFO stadiumInfo,S.STADIUM_ADDR stadiumAddr
	FROM STADIUM S
	WHERE S.STADIUM_INFO LIKE #{search}
	OR S.STADIUM_ADDR LIKE #{search}
	limit #{startRow}, #{pageSize}

  SELECT COUNT(*)
	FROM STADIUM S 
		WHERE S.STADIUM_INFO LIKE #{value}
		OR S.STADIUM_ADDR LIKE #{value}

SELECT STADIUM_NAME,STADIUM_INFO,STADIUM_PHOTO,A.AREA_NAME
FROM STADIUM S JOIN AREA A
	ON S.AREA_INDEX LIKE A.AREA_INDEX;
